<?phprequire 'DB.php';class CheckView{	public $db_voice_table = 'voice_chat';	public $db_board_table = 'board';	public $db_users_table = 'users';	public  static $DB;	public function __construct() {		self::$DB = new DB();	}	public function __destruct() {		self::$DB->close();	}		function CheckConference() {	 	$sql = "SELECT closed_chat,status_chat FROM $this->db_voice_table WHERE id='1' ";		$qw=self::$DB->query($sql);		if (self::$DB->num_rows($qw)>0) {			$re = self::$DB->fetch_array($qw);				if($re['status_chat'] == 0){					return 4;				}				/*if ($re['closed_chat']==1) {						$_SESSION['user_closed_chat'] = $re['closed_chat'];						return 1;					}else{					return 2;				}*/			return $re['closed_chat']; 						}else{			return '-1';		}	}	function UsersConfereceCheck($id,$close_chat){			$id = self::$DB->escape($id);			$close_chat = self::$DB->escape($close_chat);			if($close_chat == 1){				$close_chat = 1;				}else{				$close_chat = 0;			}			$sql = "UPDATE $this->db_users_table SET closed ='$close_chat' WHERE id IN ($id) ";			$qw=self::$DB->query($sql);			if($sql==true){					return 1;				}else{					return '-1';			}	}	function UsersConfereceModeration($id,$chat){			$id = self::$DB->escape($id);			$chat = self::$DB->escape($chat);			if($chat == 1){				$chat = 1;				}else{				$chat = 0;			}			$sql = "UPDATE $this->db_users_table SET moderation ='$chat' WHERE id IN ($id) ";			$qw=self::$DB->query($sql);			if($sql==true){					return 1;				}else{					return '-1';			}	}	function EditViewUsers($id_user){			$sql = "SELECT * FROM $this->db_users_table WHERE id='$id_user' ";			$qw=self::$DB->query($sql);			$re = self::$DB->fetch_array($qw);				$sel_r[2] = '';				$sel_r[4] = '';				$sel_r[1] = '';				$sel_r[$re[role]] = 'selected';				$text_user_view = "				<tr>					<td width='120px' align='right'>					<strong>Логин:</strong>					</td>					<td  align='left'>					<input class='cforms_reg' type='text' name='name_edit' id='name_edit' value='$re[name]' />					</td>				</tr>				<tr>					<td width='120px' align='right'>					<strong>Имя(ФИО):</strong>					</td>					<td  align='left'>					<input class='cforms_reg' type='text' name='fio_edit' id='fio_edit' value='$re[fio]' />					</td>				</tr>				<tr>					<td width='120px' align='right'>					<strong>Телефон:</strong>					</td>					<td  align='left'>					<input class='cforms_reg' type='text' name='phone_edit' id='phone_edit' value='$re[phone]' />					</td>				</tr>				<tr>					<td align='right'>					<strong>e-mail:</strong>					</td>					<td align='left'>					<input class='cforms_reg'  type='text' name='mail_edit' id='mail_edit' value='$re[mail]' />					</td>				</tr>								<tr>					<td align='right'>					<strong>Новый пароль:</strong>					</td>					<td align='left'>					<input class='cforms_reg'  type='text' name='pass_edit' id='pass_edit'  />					</td>				</tr>				<tr>					<td align='right'>					<strong>Выберите тип пользователя:</strong>					</td>					<td align='left'>					<select class='cforms_sel'  name='sel_role_edit' id='sel_role_edit' >					";					$text_user_view .= "					<option $sel_r[2] value='2'>Пользователь</option>					<option $sel_r[4] value='4'>Speaker</option>					<option $sel_r[1] value='1'>Администратор</option>					</select>					</td>				</tr>				<tr>					<td colspan='2' align='center'>					</td>				</tr>				<tr>					<td colspan='2' align='center'>					<input name='id_edit' id='id_edit' type='hidden' value='$re[id]'>					</td>				</tr>";				return $text_user_view;	}								function NewUsersView($name,$phone,$pass,$mail,$role,$fio){			$name = self::$DB->escape($name);			$pass = self::$DB->escape($pass);			$mail = self::$DB->escape($mail);			$role = self::$DB->escape($role);			$fio = self::$DB->escape($fio);			$upass = $pass;			$pass = md5(md5($pass));			$sql = "SELECT name FROM $this->db_users_table WHERE name='$name' ";			$qw=self::$DB->query($sql);			if (self::$DB->num_rows($qw)>0) {				return '-2';			}						$dat_reg = date('Y-m-d H:i:s');			$sql = "INSERT INTO $this->db_users_table (name,fio,phone,pass,upass,mail,dat_reg,role) 							VALUES ('$name','$fio','$phone','$pass','$upass','$mail','$dat_reg','$role')";			$qw=self::$DB->query($sql);			if($sql==true){					return 1;				}else{					return '-1';			}	}	function AddNewsBoard($text){			$text = self::$DB->escape($text);			$dat = date('Y-m-d H:i:s');			$sql = "INSERT INTO $this->db_board_table (text) VALUES ('$text')";			$qw=self::$DB->query($sql);			if($sql==true){				return 1;				}else{					return '-1';			}		}		function NewUsersUpd($name,$fio,$phone,$pass,$mail,$role,$id){			$name = self::$DB->escape($name);			$pass = self::$DB->escape($pass);			$mail = self::$DB->escape($mail);			$role = self::$DB->escape($role);			$id = self::$DB->escape($id);			$fio = self::$DB->escape($fio);			$phone = self::$DB->escape($phone);			if($pass!=''){				$upass = $pass;				$pass = md5(md5($pass));				$sql = "UPDATE $this->db_users_table 							SET name='$name',fio='$fio',phone='$phone',pass='$pass',upass='$upass',mail='$mail',role='$role' 							WHERE id ='$id' ";				}else{				$sql = "UPDATE $this->db_users_table 							SET name='$name',fio='$fio',phone='$phone',mail='$mail',role='$role' 							WHERE id ='$id' ";				}			$qw=self::$DB->query($sql);			if($sql==true){					return 1;				}else{					return '-1';			}	}		function NameUsersView($id){			$arr_us = array();			$id = self::$DB->escape($id);			$sql = "SELECT name FROM $this->db_users_table WHERE id='$id' LIMIT 1 ";			$qw=self::$DB->query($sql);			if (self::$DB->num_rows($qw)>0) {				$re = self::$DB->fetch_array($qw);				$arr_us[$id] .=$re['name'];				return $arr_us;			}else{				return 'Error Users';			}					}					function UsersDelete($users_id){			$sql = "DELETE FROM $this->db_users_table WHERE id IN ($users_id) ";			$qw=self::$DB->query($sql);			if($sql==true){				return 1;				}else{					return '-1';			}		}		function NewsDelete($id){			$sql = "DELETE FROM $this->db_board_table WHERE id = '$id' ";			$qw=self::$DB->query($sql);			if($sql==true){				return 1;				}else{					return '-1';			}		}		function EditNews($id_news,$text_news_admin){			$sql = "UPDATE $this->db_board_table SET text='$text_news_admin' WHERE  id = '$id_news' ";			$qw=self::$DB->query($sql);			if($sql==true){				return 1;				}else{					return '-1';			}		}			function ViewNewsText($id){				$sql = "SELECT id,text FROM $this->db_board_table WHERE id='$id' ORDER BY id DESC ";				$qw=self::$DB->query($sql);					if (self::$DB->num_rows($qw)>0) {					$re = self::$DB->fetch_array($qw);							$text_user_view = "								<tr>								<script src='ckeditor/ckeditor.js' type='text/javascript'></script>									<td align='left'>									<textarea name='news_text_edit' id='news_text_edit'>$re[text]</textarea>	                                <script>	                                    CKEDITOR.replace( 'news_text_edit' );	                                </script>									<input name='news_id_edit' id='news_id_edit' type='hidden' value='$re[id]'>										</td>								</tr>";					}								return $text_user_view;		}		function ViewAddTextNews(){						$text_user_view = "								<tr>								<script src='ckeditor/ckeditor.js' type='text/javascript'></script>									<td align='left'>									<textarea name='news_add_edit' id='news_add_edit'>$re[text]</textarea>	                                <script>	                                    CKEDITOR.replace( 'news_add_edit' );	                                </script>									<input name='news_id_edit' id='news_id_edit' type='hidden' value='$re[id]'>										</td>								</tr>";													return $text_user_view;		}	function NewsBoardUpdate($text,$id){			$text = self::$DB->escape($text);			$id = self::$DB->escape($id);			$sql = "UPDATE $this->db_board_table SET text='$text' WHERE id ='$id' ";			$qw=self::$DB->query($sql);			if($sql==true){					return 1;				}else{					return '-1';			}	}		function ViewNewsBoard(){			$sql = "SELECT id,text FROM $this->db_board_table ORDER BY id DESC";			$qw=self::$DB->query($sql);				if (self::$DB->num_rows($qw)>0) {					$i_count = self::$DB->num_rows($qw);					while($re = self::$DB->fetch_array($qw)){						echo"							<tr class= 'tablnews_view_tr'>							<td valign='top' align='center' width='35'>							<a href='#' onClick='edit_news($re[id]);'><img src='images/edit.png' /></a> 							<a href='#' onClick='del_news($re[id]);'><img src='images/cross.png' /></a>												</td>							<td align='left' >$re[text]</td>							</tr>";						$i_count= $i_count - 1;						}				}								return $text_news_view;		}		function OnlineUser(){			$sql = "SELECT id FROM $this->db_users_table WHERE ses!='0'&&role!='1' ";			$qw=self::$DB->query($sql);			$i_count = self::$DB->num_rows($qw);			return $i_count;		}	function OutMail($name_out,$mail_out,$quest_out){			$mail_site = "info@teletrade.tv";			$subject = 'Сообщение сайта';// тема письма			$subject = "=?utf-8?b?" .base64_encode($subject). "?="; 			$headers  = 'MIME-Version: 1.0' . "\r\n";			$headers .= 'Content-type: text/html; charset=utf-8' . "\r\n";			$headers .= "From: Site Invest Club <info@teletrade.tv>\r\n";				$message = "			<html>			<head>			<meta http-equiv='Content-Type' content='text/html; charset=utf-8' />			</head>			<body>			<hr>			Имя: $name_out			<br>			е-mail: $mail_out			<br />			Вопрос: $quest_out			<br />			<hr>						<body>			</html>			";		return mail($mail_site, $subject, $message, $headers);	}}?>